import React, { Component } from 'react'
import PropTypes from 'prop-types'

class ThemeSwitch extends Component {
  static contextTypes = {
    store: PropTypes.object
  }
  constructor(props) {
    super(props)
    this.state = {
      themeColor: ''
    }
    this.changeRed = this.changeRed.bind(this)
  }
  componentDidMount() {
    this._updateColor()
  }
  _updateColor() {
    const { store } = this.context
    const state = store.getState()
    this.setState({
      themeColor: state.themeColor
    })
  }
  changeRed() {
    const { store } = this.context
    store.dispatch({
      type: 'CHANGE_COLOR',
      themeColor: 'red'
    })
  }
  changeBlue() {
    const { store } = this.context
    store.dispatch({
      type: 'CHANGE_COLOR',
      themeColor: 'blue'
    })
  }
  render () {
    return (
      <div >
        <button style={{color: this.state.themeColor}} onClick={this.changeRed}>Red</button>
        <button style={{color: this.state.themeColor}} onClcik={this.changeBlue}>Blue</button>
      </div>
    )
  }
}

export default ThemeSwitch
