import React, { Component } from 'react'
import PropTypes from 'prop-types'

const connect = mapStateToProps => WrapperComponent => {
  return class extends Component {
    static contextTypes = {
      store: PropTypes.object
    }

    constructor(props) {
      super(props)
      this.state = {
        allProps: {}
      }
    }

    componentWillMount() {
      const { store } = this.context
      this._updateStore()
      store.subscribe(() => {
        this._updateStore()
      })
    }

    _updateStore() {
      const { store } = this.context
      const letState = mapStateToProps(store.getState(), this.props)
      this.setState({
        allProps: {
          ...letState,
          ...this.props
        }
      })
    }

    render() {
      return (
        <WrapperComponent {...this.state.allProps} />
      )
    }
  }
}

export { connect }
